  };
  useEffect(iRunOnlyOnce, []); */
  useEffect(() => {
    console.log("CALL THE API. . . .");
  }, []);
  return (
    <div>
      <input type="text" placeholder="Search here. . ." />
      <h1>{counter}</h1>
      <button onClick={onClick}>click me</button>
    </div>
  );
}

export default App;
